INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'Mr WordPress', '', 'https://wordpress.org/', '', '2016-02-16 14:14:36', '2016-02-16 14:14:36', 'Hi, this is a comment.\r\nTo delete a comment, just log in and view the post&#039;s comments. There you will have the option to edit or delete them.', 0, 'post-trashed', '', '', 0, 0),
(2, 41, 'w01_class_root', 'christack50@gmail.com', '', '127.0.0.1', '2016-02-17 15:40:44', '2016-02-17 15:40:44', 'Superbe image... Elle aide a comprendre l''histoire de cette ville, boustée par la sidérurgie et facilitée par les avantages du fleuve.', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/48.0.2564.103 Safari/537.36', '', 0, 1),
(3, 41, 'w01_class_root', 'christack50@gmail.com', '', '127.0.0.1', '2016-02-17 15:41:48', '2016-02-17 15:41:48', 'C''est vrai pour Liège comme pour beaucoup d''autres villes dans le monde.', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/48.0.2564.103 Safari/537.36', '', 0, 1),
(4, 41, 'w01_class_root', 'christack50@gmail.com', '', '127.0.0.1', '2016-02-17 15:43:43', '2016-02-17 15:43:43', 'Oui  ais cela n''explique pas tout!!!\r\nLe fleuve passait a beaucoup d''endroits et pourquoi Liège s''est développé et pas d''autres villes ??', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/48.0.2564.103 Safari/537.36', '', 0, 1),
(5, 41, 'w01_class_root', 'christack50@gmail.com', '', '127.0.0.1', '2016-02-17 15:44:09', '2016-02-17 15:44:09', 'On peut se poser la question... En effet !!!', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/48.0.2564.103 Safari/537.36', '', 0, 1);
